  @impl ExJsonSchema.Validator
  @spec validate(
          root :: Root.t(),
          schema :: ExJsonSchema.data(),
          property :: {String.t(), ExJsonSchema.data()},
          data :: ExJsonSchema.data()
        ) :: Validator.errors_with_list_paths()
  def validate(_, _, {"format", format}, data) do
    do_validate(format, data)
  end

  def validate(_, _, _, _) do
    []
  end

  defp do_validate(_, data) when not is_bitstring(data) do
    []
  end

  defp do_validate("date", data) do
    validate_with_regex(data, @date_regex, fn data ->
      "Expected #{inspect(data)} to be a valid date."
    end)
  end

  defp do_validate("date-time", data) do
    validate_with_regex(data, @date_time_regex, fn data ->
      "Expected #{inspect(data)} to be a valid ISO 8601 date-time."
    end)
  end

  defp do_validate("time", data) do
    validate_with_regex(data, @time_regex, fn data ->
      "Expected #{inspect(data)} to be a valid time."
    end)
  end

  defp do_validate("email", data) do
    validate_with_regex(data, @email_regex, fn data ->
      "Expected #{inspect(data)} to be an email address."
    end)
  end

  defp do_validate("hostname", data) do
    validate_with_regex(data, @hostname_regex, fn data ->
      "Expected #{inspect(data)} to be a host name."
    end)
  end

  defp do_validate("ipv4", data) do
    validate_with_regex(data, @ipv4_regex, fn data ->
      "Expected #{inspect(data)} to be an IPv4 address."
    end)
  end

  defp do_validate("ipv6", data) do
    validate_with_regex(data, @ipv6_regex, fn data ->
      "Expected #{inspect(data)} to be an IPv6 address."
    end)
  end

  defp do_validate("idn-hostname", data) do
    validate_with_regex(data, @idn_hostname_regex, fn data ->
      "Expected #{inspect(data)} to be an idn email."
    end)
  end

  defp do_validate("idn-email", data) do
    validate_with_regex(data, @idn_email_regex, fn data ->
      "Expected #{inspect(data)} to be an idn email."
    end)
  end

  defp do_validate("iri", data) do
    validate_with_regex(data, @iri_regex, fn data -> "Expected #{inspect(data)} to be an iri." end)
  end

  defp do_validate("iri-reference", data) do
    validate_with_regex(data, @iri_reference_regex, fn data ->
      "Expected #{inspect(data)} to be an iri."
    end)
  end

  defp do_validate("uri", data) do
    validate_with_regex(data, @uri_regex, fn data -> "Expected #{inspect(data)} to be a uri." end) ++
      validate_with_regex(data, @uri_reference_regex, fn data ->
        "Expected #{inspect(data)} to be a uri reference."
      end)
  end

  defp do_validate("uri-reference", data) do
    validate_with_regex(data, @uri_reference_regex, fn data ->
      "Expected #{inspect(data)} to be a uri reference."
    end)
  end

  defp do_validate("uri-template", data) do
    validate_with_regex(data, @uri_template_regex, fn data ->
      "Expected #{inspect(data)} to be a uri template."
    end)
  end

  defp do_validate("json-pointer", data) do
    validate_with_regex(data, @json_pointer_regex, fn data ->
      "Expected #{inspect(data)} to be a json pointer."
    end)
  end

  defp do_validate("relative-json-pointer", data) do
    validate_with_regex(data, @relative_json_pointer_regex, fn data ->
      "Expected #{inspect(data)} to be a json pointer."
    end)
  end

  defp do_validate("regex", data) do
    if Regex.match?(@z_anchor, data) do
      [{"Regex does not support Z anchor", []}]
    else
      []
    end
  end

  defp do_validate(_, _) do
    []
  end

  defp validate_with_regex(data, regex, failure_message_fun) do
    if Regex.match?(regex, data) do
      []
    else
      [{failure_message_fun.(data), []}]
    end
  end
end
